sition="before"><![CDATA[\r\n\t\t\t\t*/\r\n\r\n\t\t\t\tif($categories) {\r\n\t\t\t\t\t$catalog[] = array(\r\n\t\t\t\t\t\t\'name\'\t   => $this->language->get(\'text_category\'),\r\n\t\t\t\t\t\t\'href\'     => \'\',\r\n\t\t\t\t\t\t\'children\' => $categories\r\n\t\t\t\t\t);\r\n\t\t\t\t}\r\n\t\t\t]]></add>\r\n\t\t</operation>\r\n\t</file>\r\n</modification>', '1', '2016-08-22 13:45:40');
